data.command:
if (args[0].tag === "DamageBonus") {
    const lastArg = args[args.length -1];
    console.log(lastArg);
    let tokenD = canvas.tokens.get(lastArg.tokenId);
    let actorD = tokenD.actor;
    let actorData = actorD.getRollData();
    let colItem = actorD.items.getName("Colossus Slayer");
    let itemD = lastArg.item;
    let target = canvas.tokens.get(lastArg.targets[0].id);
    let targetData = target.actor.getRollData();    
    if(targetData.attributes.hp.value === targetData.attributes.hp.max) return {};
    if (!["ak"].some(i=> (itemD.data.actionType).toLowerCase().includes(i))) return {};    
    let damageType = lastArg.damageDetail[0].type;
    let damageRoll = await new game.dnd5e.dice.DamageRoll(`1d8[${damageType}]`, actorData, { critical: lastArg.isCritical }).evaluate();    
    if (actorD.effects.find(i => i.data.label === `${colItem.name} Attack`)) return {};
    let gameRound = game.combat ? game.combat.round : 0;
    let effectData = {
        label: `${colItem.name} Attack`,
        icon: colItem.img,
        tint: "#1eff00",
        origin: colItem.uuid,
        duration: { turns: 1, startRound: gameRound, startTime: game.time.worldTime }
    };    
    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: actorD.uuid, effects: [effectData] });    
    return { damageRoll: damageRoll.formula, flavor: `(${colItem.name} (${CONFIG.DND5E.damageTypes[damageType]}))` };
}

macro.command:
if (args[0].tag === "DamageBonus") {
    const lastArg = args[args.length - 1];
    const version = Math.floor(game.version);
    let tokenD = canvas.tokens.get(lastArg.tokenId);
    let actorD = tokenD.actor;
    let actorData = await actorD.getRollData();
    let colItem = actorD.items.getName("Colossus Slayer");
    let itemD = lastArg.item;
    let target = canvas.tokens.get(lastArg.targets[0].id);
    let targetData = await target.actor.getRollData();
    if (targetData.attributes.hp.value >= targetData.attributes.hp.max) return {};
    if (!["ak"].some(i => (version > 9 ? itemD.system.actionType : itemD.data.actionType).toLowerCase().includes(i))) return {};
    let damageType = lastArg.damageDetail[0].type;
    let damageRoll = await new game.dnd5e.dice.DamageRoll(`1d8[${damageType}]`, actorData, { critical: lastArg.isCritical }).evaluate();
    if (actorD.effects.find(i => (version > 9 ? i.label : i.data.label) === `${colItem.name} Attack`)) return {};
    let gameRound = game.combat ? game.combat.round : 0;
    let effectData = {
        label: `${colItem.name} Attack`,
        icon: colItem.img,
        tint: "#1eff00",
        transfer: false,
        flags : { "dae": { "token": actorD.uuid, specialDuration: ["turnStartSource","turnEndSource"] } },
        origin: colItem.uuid,
        duration: { turns: 1, startRound: gameRound, startTime: game.time.worldTime }
    };
    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: actorD.uuid, effects: [effectData] });
    return { damageRoll: damageRoll.formula, flavor: `(${colItem.name} (${CONFIG.DND5E.damageTypes[damageType]}))` };
}
